                    uri: result.uri,
                    repoName: result.repoName,
                    revision: result.commit,
                    source: ContextItemSource.Unified,
                    content: result.content,
                    range: {
                        start: {
                            line: result.startLine,
                            character: 0,
                        },
                        end: {
                            line: result.endLine,
                            character: 0,
                        },
