            const symbolCaptures = []
            const rangeCaptures = []

            for (const capture of captures) {
                if (capture.name.startsWith('range')) {
                    rangeCaptures.push(capture)
                } else if (capture.name.startsWith('symbol')) {
                    symbolCaptures.push(capture)
