        const bfg = await this.loadedBFG
        const indexingStartTime = Date.now()
        // TODO: include commit?
        try {
            if (repository) {
                await bfg.request('bfg/gitRevision/didChange', {
                    gitDirectoryUri: repository.uri.toString(),
