    private async statIndex(scopeDir: FileURI): Promise<CorpusDiff | null> {
        const { indexDir } = this.getIndexDir(scopeDir)
        const { symfPath } = await this.getSymfInfo()
        try {
            const { stdout } = await execFile(symfPath, [
