            const selection = new vscode.Selection(startLine, 0, range.end.line + 1, 0)

            codeLenses.push(
                new vscode.CodeLens(range, {
                    ...commandLenses.test,
                    arguments: [{ name: 'cody.command.tests-cases', selection }],
                })
            )

            linesWithLenses.add(startLine)
        }

