        this.context.subscriptions.push(
            git.onDidOpenRepository(repository => this.didChangeGitExtensionRepository(repository))
        )
        this.context.subscriptions.push(
            vscode.workspace.onDidChangeWorkspaceFolders(() => this.indexInferredGitRepositories())
