                    // Get last 10 identifiers from the current document prefix.
                    ...getLastNGraphContextIdentifiersFromDocument({
                        n: 10,
                        document,
                        position,
                        currentLinePrefix: docContext.currentLinePrefix,
                    }),
                ])
            )

