func (l *concurrencyLimiter) TryAcquire(ctx context.Context) (func(context.Context, int) error, error) {
	commit, err := (limiter.StaticLimiter{
		LimiterName:        "actor.concurrencyLimiter",
		Identifier:         l.actor.ID,
