func limitMatches(files []FileMatch, limit int, chunkMatches bool) ([]FileMatch, int) {
	var limiter func(file *FileMatch, limit int) int
	if chunkMatches {
		limiter = limitChunkMatches
	} else {
		limiter = limitLineMatches
	}
	for i := range files {
		limit = limiter(&files[i], limit)
		if limit <= 0 {
			return files[:i+1], 0
		}
	}
	return files, limit
