func NewDisplayTruncator(opts *SearchOptions) (_ DisplayTruncator, hasLimits bool) {
	docLimit := opts.MaxDocDisplayCount
	docLimited := docLimit > 0

	matchLimit := opts.MaxMatchDisplayCount
	matchLimited := matchLimit > 0

