			}

			cm.Ranges = cm.Ranges[:limit]
			if cm.SymbolInfo != nil {
				// When non-nil, SymbolInfo is specified to have the same length
				// as Ranges.
				cm.SymbolInfo = cm.SymbolInfo[:limit]
			}
		}
		if len(cm.Ranges) == limit {
			file.ChunkMatches = file.ChunkMatches[:i+1]
			limit = 0
			break
		}
		limit -= len(cm.Ranges)
	}
	return limit
}

