	a.log.Printf("starting agent")

	// Start the webserver.
	a.wg.Add(1)
	go func() {
		defer a.wg.Done()
		if err := a.webserver.Run(); err != nil {
			a.log.Printf("webserver failed: %v", err)
		}
	}()

	// Start the mirror.
	a.wg.Add(1)
